DNA=sys.argv[1]
max_length=sys.argv[2]

def dnaformat(dna,max_length):
	dna=dna.replace('\n','')
#	max_length=int(max_length)
	lines=re.findall(r'\w{0,'+re.escape(max_length)+r'}',dna)	
	print('\n'.join(lines))
